package ifelse;

public class evenoddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int num=3;

		    if (num%2 == 0)
		    {
		      System.out.println("The number is even.");
		    }
		    else
		    {
		      System.out.println("The number is odd.");
		  }


	}

}
